package datas;

public class String1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String s1 = "Hello ";
		String s2 = new String("I am ");
		char s3[] = {'H','e','r','e'};
		
		System.out.println(s1);
		System.out.println(s2);
	
		for (char c : s3) {
			System.out.print(c);
		}
		
	}

}
